dunes-script-module.script:
var retryCount = 0;
while (retryCount < totalRetries) {
	try {

		return func();
	} catch (e) {
		if (retryIfCondition(e)) {
			System.log("Retrying function call...");
			retryCount++;
			System.sleep(500);
		} else {
			throw e;
		}
	}
}
throw "Unable to execute function within " + totalRetries + " retries";
