workflow-item.script:
var gpdbClusters;
var cat = Server.getConfigurationElementCategoryWithPath("Samsung Bigdata");
for each (e in cat.configurationElements){
	if (e.name == "GPDB Clusters"){
		gpdbClusters = e;
		break;
	}
}

if (!gpdbClusters){
	throw "Configuration element at path 'Samsung Bigdata/GPDB Clusters' cannot be found."
}

if (relationName == "namespace-children") {
	resultObjs = new Array();

	var clusters = gpdbClusters.attributes;
	clusters.forEach( function(cluster) {
		var clusterSettings = JSON.parse(cluster.value);
		var object = DynamicTypesManager.makeObject("Samsung Bigdata", "GPDB Cluster", clusterSettings.clusterId, cluster.name);
		object.setProperty("mdwId", clusterSettings.mdwId);
		object.setProperty("smdwId", clusterSettings.smdwId);
		object.setProperty("sdwIds", clusterSettings.sdwIds);
		resultObjs.push(object);
	});
}

if (relationName == "GPDB Cluster-Member") {
	resultObjs = new Array();

	var clusters = gpdbClusters.attributes;
	clusters.forEach( function(cluster){
		var clusterSettings = JSON.parse(cluster.value);
		if (clusterSettings.clusterId == parentId){
			var members = clusterSettings.members;
			if (members){
				members.forEach ( function(member){
					var object = DynamicTypesManager.makeObject("Samsung Bigdata", "GPDB Member", member.id, member.fqdn);
					object.setProperty("role", member.role);
					object.setProperty("ipAddress", member.ipAddress);
					object.setProperty("fqdn", member.fqdn);
					resultObjs.push(object);
				});
			}
		}
	});
}
